# Find-LongestDecreasingSubsequence calculates the length of the longest decreasing subsequence (LDS) of a list of intervals.
# The intervals are first sorted by their left endpoint in increasing order, and then the LDS is found based on the right endpoints.
function Find-LongestDecreasingSubsequence {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [array]$Intervals
    )

    # Sort intervals by left endpoint (L) in increasing order, then by right endpoint (R) in increasing order
    $sortedIntervals = $Intervals | Sort-Object -Property @{Expression={$_[0]}; Ascending=$true}, @{Expression={$_[1]}; Ascending=$true}

    # Initialize an array to keep track of the smallest tail of all increasing subsequences
    $tails = @()

    foreach ($interval in $sortedIntervals) {
        $R = -$interval[1]  # Using negative to simulate bisect_right for decreasing sequence

        # Find the first index in tails where tails[index] > R (since tails is sorted in ascending order)
        $idx = [System.Array]::BinarySearch($tails, $R)
        if ($idx -lt 0) {
            $idx = -$idx - 1
        }

        if ($idx -eq $tails.Count) {
            $tails += $R
        } else {
            $tails[$idx] = $R
        }
    }

    return $tails.Count
}

