# Calculate-BellNumbers computes Bell numbers up to a given maximum value using dynamic programming.
# Bell numbers are a sequence of numbers that represent the number of partitions of a set with n elements.
# The function returns an array where each element at index i represents the Bell number for i.
function Calculate-BellNumbers {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$MaxN
    )

    $MOD = 95041567
    $bell = @(0) * ($MaxN + 1)
    for ($i = 0; $i -le $MaxN; $i++) {
        $bell[$i] = @(0) * ($MaxN + 1)
    }

    $bell[0][0] = 1
    for ($n = 1; $n -le $MaxN; $n++) {
        $bell[$n][0] = $bell[$n-1][$n-1]
        for ($k = 1; $k -le $n; $k++) {
            $bell[$n][$k] = ($bell[$n][$k-1] + $bell[$n-1][$k-1]) % $MOD
        }
    }

    $result = @(0) * ($MaxN + 1)
    for ($i = 0; $i -le $MaxN; $i++) {
        $result[$i] = $bell[$i][0]
    }

    return $result
}

