# Function to calculate the maximum number of bit flip operations required to ensure no two '1's are adjacent
function Get-MaxBitFlipOperations {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string]$BinaryString
    )

    $operations = 0
    $n = $BinaryString.Length
    $i = 0

    # Traverse the binary string
    while ($i -lt $n) {
        # If current character is '1' and next character is '1', we have to flip one of them
        if ($i + 1 -lt $n -and $BinaryString[$i] -eq '1' -and $BinaryString[$i + 1] -eq '1') {
            $operations++
            # Skip the next character to avoid consecutive '1's
            $i += 2
        }
        else {
            $i++
        }
    }

    return $operations
}

