# Find-ClosestTriplet finds the triplet in an array whose sum is closest to zero.
# The function sorts the array first, then uses a two-pointer technique to efficiently find the closest triplet.
# If a triplet with sum zero is found, it returns immediately for early exit.
function Find-ClosestTriplet {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Array
    )

    $n = $Array.Count
    $sortedArray = $Array | Sort-Object
    $closest = @()
    $minDiff = [double]::MaxValue

    for ($i = 0; $i -lt $n - 2; $i++) {
        $left = $i + 1
        $right = $n - 1
        while ($left -lt $right) {
            $currentSum = $sortedArray[$i] + $sortedArray[$left] + $sortedArray[$right]
            $currentAbs = [Math]::Abs($currentSum)
            if ($currentAbs -lt $minDiff) {
                $minDiff = $currentAbs
                $closest = @($sortedArray[$i], $sortedArray[$left], $sortedArray[$right])
                if ($minDiff -eq 0) {
                    # Early exit if sum is zero
                    return $closest
                }
            }
            if ($currentSum -lt 0) {
                $left++
            } else {
                $right--
            }
        }
    }

    return $closest
}

