class ArrayManipulator {
    [int]$n
    [int[]]$arr
    [int[]]$lazy

    # Constructor initializes the array and the lazy propagation array
    ArrayManipulator([int[]]$arr) {
        $this.n = $arr.Count
        $this.arr = $arr
        $this.lazy = New-Object int[] ($this.n + 1)
    }

    # Add a value to all elements in the range [l, r] (1-based indexing)
    [void] Add([int]$l, [int]$r, [int]$value) {
        $this.lazy[$l - 1] += $value
        if ($r -lt $this.n) {
            $this.lazy[$r] -= $value
        }
    }

    # Calculate the sum of elements in the range [l, r] (1-based indexing)
    [int] Sum([int]$l, [int]$r) {
        $currentAddition = 0
        $result = 0
        for ($i = $l - 1; $i -lt $r; $i++) {
            $currentAddition += $this.lazy[$i]
            $result += $this.arr[$i] + $currentAddition
        }
        return $result
    }
}

