# PairPaintingsWithFrames matches paintings to frames based on size constraints.
# Paintings are sorted by value (ascending) and then by size (ascending).
# Frames are sorted by size (ascending).
# The function counts how many paintings can be matched with frames where the frame size is >= painting size.
function PairPaintingsWithFrames {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [array]$Paintings,

        [Parameter(Mandatory = $true)]
        [int[]]$Frames
    )

    # Sort paintings by value (ascending) and then by size (ascending)
    $sortedPaintings = $Paintings | Sort-Object -Property @{Expression = {$_.Value}; Ascending = $true}, @{Expression = {$_.Size}; Ascending = $true}

    # Sort frames by size (ascending)
    $sortedFrames = $Frames | Sort-Object

    $i = $j = $count = 0
    $n = $sortedPaintings.Count
    $m = $sortedFrames.Count

    while ($i -lt $n -and $j -lt $m) {
        $painting = $sortedPaintings[$i]
        if ($painting.Size -le $sortedFrames[$j]) {
            $count++
            $i++
            $j++
        }
        else {
            $j++
        }
    }

    return $count
}

