# Calculate-MinRemovalsForDecreasingSequence calculates the minimum number of removals required to form a decreasing sequence from an array of numbers.
# It uses dynamic programming to find the longest decreasing subsequence (LDS) and then subtracts its length from the total number of elements.
function Calculate-MinRemovalsForDecreasingSequence {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Numbers
    )

    $n = $Numbers.Count
    if ($n -eq 0) {
        return 0
    }

    # Initialize dp array where dp[i] represents the length of the LDS ending at index i
    $dp = @(1) * $n

    for ($i = 1; $i -lt $n; $i++) {
        for ($j = 0; $j -lt $i; $j++) {
            if ($Numbers[$j] -gt $Numbers[$i]) {
                if ($dp[$i] -lt ($dp[$j] + 1)) {
                    $dp[$i] = $dp[$j] + 1
                }
            }
        }
    }

    $maxLength = ($dp | Measure-Object -Maximum).Maximum
    return $n - $maxLength
}

