function Get-MaxPowerValue {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$T,

        [Parameter(Mandatory = $true)]
        [array]$TestCases
    )

    $results = @()
    for ($i = 0; $i -lt $T; $i++) {
        $N = $TestCases[$i][0][0]
        $P = $TestCases[$i][0][1]
        $cards = $TestCases[$i][1]

        $dp = New-Object int[] ($P + 1)

        foreach ($card in $cards) {
            for ($j = $P; $j -ge $card; $j--) {
                if ($dp[$j - $card] + $card -gt $dp[$j]) {
                    $dp[$j] = $dp[$j - $card] + $card
                }
            }
        }

        $results += $dp[$P]
    }

    return $results
}

