function Predict-Collision {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [System.Tuple[int, int][]]
        $Positions,

        [Parameter(Mandatory = $true)]
        [System.Tuple[int, int][]]
        $Velocities,

        [Parameter(Mandatory = $true)]
        [int]
        $T
    )

    for ($i = 0; $i -lt $Positions.Count; $i++) {
        for ($j = $i + 1; $j -lt $Positions.Count; $j++) {
            # Calculate the future positions
            $pos1_x = $Positions[$i].Item1 + $Velocities[$i].Item1 * $T
            $pos1_y = $Positions[$i].Item2 + $Velocities[$i].Item2 * $T
            $pos2_x = $Positions[$j].Item1 + $Velocities[$j].Item1 * $T
            $pos2_y = $Positions[$j].Item2 + $Velocities[$j].Item2 * $T

            # Check if the two positions are the same
            if ($pos1_x -eq $pos2_x -and $pos1_y -eq $pos2_y) {
                return $true
            }
        }
    }

    return $false
}

