function Test-PalindromeFormation {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string]$InputString
    )

    # Count the frequency of each character in the string
    $charCount = @{}
    foreach ($char in $InputString.ToCharArray()) {
        if ($charCount.ContainsKey($char)) {
            $charCount[$char]++
        } else {
            $charCount[$char] = 1
        }
    }

    # Check how many characters have an odd count
    $oddCount = 0
    foreach ($value in $charCount.Values) {
        if ($value % 2 -ne 0) {
            $oddCount++
        }
    }

    # If more than one character has an odd count, it can't form a palindrome
    if ($oddCount -gt 1) {
        return ""
    }

    # Build the palindrome
    $mid = ""
    $half = ""
    foreach ($entry in $charCount.GetEnumerator()) {
        $char = $entry.Key
        $freq = $entry.Value
        if ($freq % 2 -ne 0) {
            $mid = $char
        }
        $half += $char * ($freq / 2)
    }

    # Return the palindrome
    return $half + $mid + (-join $half[$half.Length..0])
}

