# Build-DigitGraph constructs a directed graph from the input strings where each digit is a node,
# and edges represent the order of digits within each string.
# It returns three objects: nodes (digits), outgoing edges, and in-degree counts for each node.
function Build-DigitGraph {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string[]]$NumberStrings
    )

    $nodes = @()
    $outgoingEdges = @()
    $inDegree = @()

    foreach ($numStr in $NumberStrings) {
        $digits = $numStr.ToCharArray()
        $prevIndex = $null

        foreach ($d in $digits) {
            $nodeIndex = $nodes.Count
            $nodes += $d
            $outgoingEdges += ,@()
            $inDegree += 0

            if ($prevIndex -ne $null) {
                $outgoingEdges[$prevIndex] += $nodeIndex
                $inDegree[$nodeIndex]++
            }

            $prevIndex = $nodeIndex
        }
    }

    return $nodes, $outgoingEdges, $inDegree
}

# Get-LexicographicalOrder performs a topological sort using a min-heap to ensure
# the smallest lexicographical order of digits while respecting the constraints.
function Get-LexicographicalOrder {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [char[]]$Nodes,

        [Parameter(Mandatory = $true)]
        [array]$OutgoingEdges,

        [Parameter(Mandatory = $true)]
        [int[]]$InDegree
    )

    $heap = [System.Collections.Generic.List[System.Tuple[char,int]]]::new()

    # Initialize heap with nodes having zero in-degree
    for ($i = 0; $i -lt $Nodes.Count; $i++) {
        if ($inDegree[$i] -eq 0) {
            $heap.Add([System.Tuple]::Create($Nodes[$i], $i))
        }
    }

    # Custom comparer for the heap to sort by character
    $comparer = [System.Collections.Generic.Comparer[System.Tuple[char,int]]]::Create({
        param($x, $y)
        $x.Item1.CompareTo($y.Item1)
    })

    $result = [System.Text.StringBuilder]::new()

    while ($heap.Count -gt 0) {
        # Sort the heap to get the smallest character
        $heap.Sort($comparer)
        $tuple = $heap[0]
        $heap.RemoveAt(0)

        $char = $tuple.Item1
        $node = $tuple.Item2

        [void]$result.Append($char)

        # Process neighbors
        foreach ($neighbor in $OutgoingEdges[$node]) {
            $inDegree[$neighbor]--
            if ($inDegree[$neighbor] -eq 0) {
                $heap.Add([System.Tuple]::Create($Nodes[$neighbor], $neighbor))
            }
        }
    }

    return $result.ToString()
}

