    $testCases = @(
        @{
            Input = @("123", "456")
            Expected = "123456"
        },
        @{
            Input = @("987", "654")
            Expected = "654987"
        },
        @{
            Input = @("111", "222", "333")
            Expected = "111222333"
        },
