        }
    )

    foreach ($testCase in $testCases) {
        $nodes, $outgoingEdges, $inDegree = Build-DigitGraph -NumberStrings $testCase.Input
        $result = Get-LexicographicalOrder -Nodes $nodes -OutgoingEdges $outgoingEdges -InDegree $inDegree

        if ($result -ne $testCase.Expected) {
            throw "Test failed for input $($testCase.Input). Expected: $($testCase.Expected), Got: $result"
        }
