function Calculate-ValidPairs {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$N,
        [Parameter(Mandatory = $true)]
        [int]$K
    )

    # Precompute f(s) for s in 2..2N
    $max_s = 2 * $N
    $f = New-Object int[] ($max_s + 2)  # 1-based or 2-based

    for ($s = 2; $s -le $max_s; $s++) {
        if ($s -le ($N + 1)) {
            $f[$s] = $s - 1
        } else {
            $f[$s] = 2 * $N - $s + 1
        }
    }

    # Compute the answer
    $ans = 0
    $t_min = [Math]::Max(2, 2 - $K)
    $t_max = [Math]::Min(2 * $N, 2 * $N - $K)

    for ($t = $t_min; $t -le $t_max; $t++) {
        $s = $t + $K
        if (2 -le $s -and $s -le 2 * $N) {
            $ans += $f[$s] * $f[$t]
        }
    }

    return $ans
}

