function Test-ContainsNearbyAlmostDuplicate {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Nums,

        [Parameter(Mandatory = $true)]
        [int]$K,

        [Parameter(Mandatory = $true)]
        [int]$T
    )

    if ($T -lt 0) {
        return $false
    }

    $numDict = @{}
    $bucketSize = $T + 1

    for ($i = 0; $i -lt $Nums.Count; $i++) {
        $num = $Nums[$i]
        $bucket = [Math]::Floor($num / $bucketSize)

        if ($numDict.ContainsKey($bucket)) {
            return $true
        }
        if ($numDict.ContainsKey($bucket - 1) -and [Math]::Abs($numDict[$bucket - 1] - $num) -lt $bucketSize) {
            return $true
        }
        if ($numDict.ContainsKey($bucket + 1) -and [Math]::Abs($numDict[$bucket + 1] - $num) -lt $bucketSize) {
            return $true
        }

        $numDict[$bucket] = $num

        if ($i -ge $K) {
            $oldBucket = [Math]::Floor($Nums[$i - $K] / $bucketSize)
            $numDict.Remove($oldBucket)
        }
    }

    return $false
}

