# Compute-Rank calculates the rank of a number when its digits are permuted in lexicographical order.
# The rank is determined by counting all permutations that are lexicographically smaller than the given number.
function Compute-Rank {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string]$NumberString
    )

    # Initialize frequency dictionary to count occurrences of each digit
    $freq = @{}
    for ($i = 0; $i -le 9; $i++) {
        $freq[$i] = 0
    }

    # Populate frequency dictionary
    foreach ($digit in $NumberString.ToCharArray()) {
        $d = [int][string]$digit
        $freq[$d]++
    }

    $rank = 1

    for ($i = 0; $i -lt $NumberString.Length; $i++) {
        $currentDigit = [int][string]$NumberString[$i]

        for ($d = 0; $d -lt $currentDigit; $d++) {
            if ($freq[$d] -eq 0) {
                continue
            }

            # Create a temporary copy of the frequency dictionary
            $tempFreq = @{}
            foreach ($key in $freq.Keys) {
                $tempFreq[$key] = $freq[$key]
            }
            $tempFreq[$d]--

            if ($tempFreq[$d] -lt 0) {
                continue
            }

            $remainingLength = $NumberString.Length - $i - 1

            if ($remainingLength -eq 0) {
                $permutations = 1
            } else {
                # Calculate numerator: factorial of remaining length
                $numerator = 1
                for ($k = 2; $k -le $remainingLength; $k++) {
                    $numerator *= $k
                }

                # Calculate denominator: product of factorials of counts
                $denominator = 1
                foreach ($count in $tempFreq.Values) {
                    if ($count -gt 1) {
                        $fact = 1
                        for ($k = 2; $k -le $count; $k++) {
                            $fact *= $k
                        }
                        $denominator *= $fact
                    }
                }

                $permutations = $numerator / $denominator
            }

            $rank += $permutations
        }

        $freq[$currentDigit]--
        if ($freq[$currentDigit] -lt 0) {
            break
        }
    }

    return $rank
}

