        @{ Input = "1234"; Expected = 1 }
    )

    foreach ($testCase in $testCases) {
        $actual = Compute-Rank -NumberString $testCase.Input
        if ($actual -ne $testCase.Expected) {
            throw "Test failed for input '$($testCase.Input)'. Expected $($testCase.Expected) but got $actual"
        }
