# PowerShell Code for Counting Valid Palindromic Dates in a Range

<#
.SYNOPSIS
    Counts the number of valid palindromic dates between two given dates in YYYYMMDD format.
.DESCRIPTION
    This script generates all possible palindromic dates (where the date is a palindrome when written as YYYYMMDD),
    checks their validity (correct month and day values), and counts how many fall between two given dates.
#>

function Get-ValidPalindromicDatesCount {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory=$true)]
        [string]$StartDate,

        [Parameter(Mandatory=$true)]
        [string]$EndDate
    )

    $count = 0

    # Validate input dates are 8 characters long
    if ($StartDate.Length -ne 8 -or $EndDate.Length -ne 8) {
        Write-Error "Dates must be in YYYYMMDD format (8 digits)"
        return
    }

    # Check all possible years from 0000 to 9999
    for ($year = 0; $year -le 9999; $year++) {
        # Format year as 4-digit string with leading zeros
        $yStr = "{0:D4}" -f $year

        # Reverse the year string to get month and day parts
        $reversed = $yStr.ToCharArray()
        [array]::Reverse($reversed)
        $reversedStr = -join $reversed

        $mStr = $reversedStr.Substring(0, 2)
        $dStr = $reversedStr.Substring(2, 2)

        # Convert month and day to integers
        $month = [int]$mStr
        $day = [int]$dStr

        # Skip if month is invalid
        if ($month -lt 1 -or $month -gt 12) {
            continue
        }

        # Determine max days in month
        $maxDay = switch ($month) {
            { $_ -in 1, 3, 5, 7, 8, 10, 12 } { 31; break }
            { $_ -in 4, 6, 9, 11 } { 30; break }
            2 { 
                # February - check for leap year
                if (($year % 400 -eq 0) -or ($year % 4 -eq 0 -and $year % 100 -ne 0)) {
                    29
                } else {
                    28
                }
                break
            }
        }

        # Skip if day is invalid for the month
        if ($day -lt 1 -or $day -gt $maxDay) {
            continue
        }

        # Construct the generated date string
        $generatedDate = $yStr + $mStr + $dStr

        # Check if date falls within the range
        if ($generatedDate -ge $StartDate -and $generatedDate -le $EndDate) {
            $count++
        }
    }

    return $count
}

assertions:
function Test-ValidPalindromicDatesCount {
    # Test case 1: 20 year span at turn of century
    $result1 = Get-ValidPalindromicDatesCount -StartDate "20000101" -EndDate "20201231"
    if ($result1 -ne 4) {
        throw "Test 1 failed: Expected 4, got $result1"
    }

    # Test case 2: Single year (2020)
    $result2 = Get-ValidPalindromicDatesCount -StartDate "20200101" -EndDate "20201231"
    if ($result2 -ne 1) {
        throw "Test 2 failed: Expected 1, got $result2"
    }

    # Test case 3: Entire possible date range
    $result3 = Get-ValidPalindromicDatesCount -StartDate "10000101" -EndDate "99991231"
    if ($result3 -ne 331) {
        throw "Test 3 failed: Expected 331, got $result3"
    }

    # Test case 4: February range including leap day
    $result4 = Get-ValidPalindromicDatesCount -StartDate "20000201" -EndDate "20000229"
    if ($result4 -ne 0) {
        throw "Test 4 failed: Expected 0, got $result4"
    }

    # Test case 5: January 2010
    $result5 = Get-ValidPalindromicDatesCount -StartDate "20100101" -EndDate "20100131"
    if ($result5 -ne 1) {
        throw "Test 5 failed: Expected 1, got $result5"
    }

    Write-Output "All tests passed successfully"
}

