function Compare-Arrays {
    param($a, $b, $tolerance = 0.0001)

    if ($a.Count -ne $b.Count) {
        return $false
    }

    for ($i = 0; $i -lt $a.Count; $i++) {
        if ([Math]::Abs($a[$i] - $b[$i]) -gt $tolerance) {
            return $false
        }
    }

    return $true
}

function Test-DecisionTreeComplexityCurve {
    # Test case 1: Small depth range (1-3)
    $X_train1 = @(1, 2, 3, 4, 5)
    $y_train1 = @(0, 1, 0, 1, 0)
    $X_test1 = @(6, 7, 8)
    $y_test1 = @(1, 0, 1)
    $trainAcc1, $testAcc1 = Get-DecisionTreeComplexityCurve -X_train $X_train1 -y_train $y_train1 -X_test $X_test1 -y_test $y_test1 -MaxDepthStart 1 -MaxDepthEnd 3
    $expectedTrain1 = @(0.8, 0.9, 1)
    $expectedTest1 = @(0.766666666666667, 1.03333333333333, 1.3)

    if (-not (Compare-Arrays $trainAcc1 $expectedTrain1)) {
        throw "Test Case 1 failed: Training accuracies don't match"
    }
    if (-not (Compare-Arrays $testAcc1 $expectedTest1)) {
        throw "Test Case 1 failed: Testing accuracies don't match"
    }

    # Test case 2: Medium depth range (1-5)
