# Find-LongestPalindromicSubstring finds the longest palindromic substring in a given string.
# A palindrome is a string that reads the same backward as forward.
# The function uses a helper function to expand around the center of potential palindromes.
function Find-LongestPalindromicSubstring {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string]$InputString
    )

    # If the input string is empty, return an empty string
    if ([string]::IsNullOrEmpty($InputString)) {
        return ""
    }

    # Helper function to expand around the center of a potential palindrome
    function Expand-AroundCenter {
        param (
            [int]$Left,
            [int]$Right
        )

        while ($Left -ge 0 -and $Right -lt $InputString.Length -and $InputString[$Left] -eq $InputString[$Right]) {
            $Left--
            $Right++
        }
        return $InputString.Substring($Left + 1, $Right - $Left - 1)
    }

    $longest = ""
    for ($i = 0; $i -lt $InputString.Length; $i++) {
        # Check for odd-length palindromes
        $palindromeOdd = Expand-AroundCenter -Left $i -Right $i
        # Check for even-length palindromes
        $palindromeEven = Expand-AroundCenter -Left $i -Right ($i + 1)
        # Determine the longest palindrome found so far
        if ($palindromeOdd.Length -gt $longest.Length) {
            $longest = $palindromeOdd
        }
        if ($palindromeEven.Length -gt $longest.Length) {
            $longest = $palindromeEven
        }
    }

    return $longest
}

