function Calculate-SumAndDifference {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$n,
        [Parameter(Mandatory = $true)]
        [int]$k
    )

    $m = [Math]::Min($n, $k)
    $s = [Math]::Floor([Math]::Sqrt($k))

    # Calculate sum1
    $sum1 = 0
    $upper = [Math]::Min($s, $m)
    for ($i = 1; $i -le $upper; $i++) {
        $sum1 += $i * [Math]::Floor($k / $i)
    }

    # Calculate sum2
    $sum2 = 0
    for ($q = 1; $q -le $s; $q++) {
        $L = [Math]::Floor($k / ($q + 1)) + 1
        $R = [Math]::Floor($k / $q)
        $current_L = [Math]::Max($L, ($s + 1))
        $current_R = [Math]::Min($R, $m)
        if ($current_L -gt $current_R) {
            continue
        }
        $cnt = $current_R - $current_L + 1
        $sum_i = ($current_L + $current_R) * $cnt / 2
        $sum2 += $q * $sum_i
    }

    $ans = $n * $k - ($sum1 + $sum2)
    return $ans
}
function Test-CalculateSumAndDifference {
    $testCases = @(
        @{ n = 10; k = 5; expected = 29 }
        @{ n = 20; k = 10; expected = 113 }
