    )

    foreach ($testCase in $testCases) {
        $result = Calculate-SumAndDifference -n $testCase.n -k $testCase.k
        if ($result -ne $testCase.expected) {
            throw "Test failed for n = $($testCase.n), k = $($testCase.k). Expected $($testCase.expected), but got $result"
        }
    }

    Write-Output "All tests passed"
}

