# Generate-Grid creates a grid structure based on the specified number of rows, columns, width, and height.
# Each cell in the grid has a border made of '+' and '-', and content lines made of '|' and '*'.
function Generate-Grid {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$Row,
        [Parameter(Mandatory = $true)]
        [int]$Col,
        [Parameter(Mandatory = $true)]
        [int]$W,
        [Parameter(Mandatory = $true)]
        [int]$H
    )

    $grid = @()
    # Each row in the grid consists of H lines of content and 1 line of border
    $totalRowsInGrid = $Row * ($H + 1) + 1
    $totalColsInGrid = $Col * ($W + 1) + 1

    for ($i = 0; $i -lt $totalRowsInGrid; $i++) {
        $line = @()
        if ($i % ($H + 1) -eq 0) {
            # This is a border line
            for ($j = 0; $j -lt $totalColsInGrid; $j++) {
                if ($j % ($W + 1) -eq 0) {
                    $line += '+'
                } else {
                    $line += '-'
                }
            }
        } else {
            # This is a content line
            for ($j = 0; $j -lt $totalColsInGrid; $j++) {
                if ($j % ($W + 1) -eq 0) {
                    $line += '|'
                } else {
                    $line += '*'
                }
            }
        }
        $gridLine = $line -join ''
        $grid += $gridLine
    }
    return $grid
}

