# Split-ArrayIntoSubarrays calculates the minimal largest sum when splitting an array into 'm' subarrays.
# The function uses binary search to efficiently determine the minimal largest sum.
function Split-ArrayIntoSubarrays {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Nums,
        [Parameter(Mandatory = $true)]
        [int]$M
    )

    # Helper function to check if the array can be split into 'm' subarrays with a maximum sum of 'maxSum'
    function CanSplit {
        param (
            [int[]]$nums,
            [int]$m,
            [int]$maxSum
        )

        $currentSum = 0
        $requiredSubarrays = 1
        foreach ($num in $nums) {
            if ($currentSum + $num -gt $maxSum) {
                $requiredSubarrays++
                $currentSum = $num
                if ($requiredSubarrays -gt $m) {
                    return $false
                }
            }
            else {
                $currentSum += $num
            }
        }
        return $true
    }

    $left = ($Nums | Measure-Object -Maximum).Maximum
    $right = ($Nums | Measure-Object -Sum).Sum

    while ($left -lt $right) {
        $mid = [Math]::Floor(($left + $right) / 2)
        if (CanSplit $Nums $M $mid) {
            $right = $mid
        }
        else {
            $left = $mid + 1
        }
    }

    return $left
}

