# Find-MaxDelayTime calculates the maximum delay time in a network of nodes connected by wires.
# The delay time between two nodes is the number of hops (edges) between them.
# The maximum delay time is the longest shortest path between any two nodes in the network (the diameter of the tree).
function Find-MaxDelayTime {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$n,

        [Parameter(Mandatory = $true)]
        [array]$wires
    )

    if ($n -eq 1) {
        return 0
    }

    # Build the adjacency list
    $graph = @{}
    for ($i = 1; $i -le $n; $i++) {
        $graph[$i] = @()
    }

    foreach ($wire in $wires) {
        $u = $wire[0]
        $v = $wire[1]
        $graph[$u] += $v
        $graph[$v] += $u
    }

    # BFS function to find the furthest node and its distance from the start node
    function BFS {
        param (
            [int]$start
        )

        $distances = @{}
        for ($i = 1; $i -le $n; $i++) {
            $distances[$i] = -1
        }

        $queue = [System.Collections.Generic.Queue[int]]::new()
        $queue.Enqueue($start)
        $distances[$start] = 0
        $furthestNode = $start
        $maxDistance = 0

        while ($queue.Count -gt 0) {
            $node = $queue.Dequeue()
            $distance = $distances[$node]

            foreach ($neighbor in $graph[$node]) {
                if ($distances[$neighbor] -eq -1) {
                    $distances[$neighbor] = $distance + 1
                    $queue.Enqueue($neighbor)
                    if ($distances[$neighbor] -gt $maxDistance) {
                        $furthestNode = $neighbor
                        $maxDistance = $distances[$neighbor]
                    }
                }
            }
        }

        return $furthestNode, $maxDistance
    }

    # Find the furthest node from any starting node (node 1)
    $furthestNode, $null = BFS -start 1

    # Find the furthest node from the furthest node found in the previous step
    $null, $maxDelay = BFS -start $furthestNode

    return $maxDelay
}
