# Test-StringSwaps checks if a given string can be transformed into 'abc' by performing at most one swap between any two characters.
# The function returns "YES" if the string is already 'abc' or can be transformed into 'abc' with a single swap, otherwise it returns "NO".
function Test-StringSwaps {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string]$InputString
    )

    if ($InputString -eq 'abc') {
        return "YES"
    }

    # Convert the string to a character array for swapping
    $charArray = $InputString.ToCharArray()

    # Check all possible single swaps
    # Swap first and second characters
    $temp = $charArray.Clone()
    $temp[0], $temp[1] = $temp[1], $temp[0]
    if (-join $temp -eq 'abc') {
        return "YES"
    }

    # Swap first and third characters
    $temp = $charArray.Clone()
    $temp[0], $temp[2] = $temp[2], $temp[0]
    if (-join $temp -eq 'abc') {
        return "YES"
    }

    # Swap second and third characters
    $temp = $charArray.Clone()
    $temp[1], $temp[2] = $temp[2], $temp[1]
    if (-join $temp -eq 'abc') {
        return "YES"
    }

    return "NO"
}

