# Calculate-SquareRoot computes the square root of a non-negative integer using the Babylonian method.
# The function approximates the square root to a specified number of decimal places.
# If the input number is 0, it immediately returns "0" formatted to the requested decimal places.
function Calculate-SquareRoot {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$X,

        [Parameter(Mandatory = $true)]
        [int]$N
    )

    if ($X -eq 0) {
        return "0".PadRight($N + 2, '0')  # +2 accounts for "0." in the output
    }

    $precision = [Math]::Pow(10, -$N)
    $guess = $X / 2.0

    while ($true) {
        $nextGuess = ($guess + $X / $guess) / 2.0
        if ([Math]::Abs($nextGuess - $guess) -lt $precision) {
            break
        }
        $guess = $nextGuess
    }

    # Format the result to N decimal places
    $formatStr = "0." + ("0" * $N)
    return $nextGuess.ToString($formatStr)
}

