        $u, $v = $edge.Item1, $edge.Item2
        $adj[$u] += $v
        $adj[$v] += $u
    }
    return $adj
}

# Get-MaxDegree calculates the maximum degree of any vertex in the graph.
# Parameters:
#   - $adj: The adjacency list representation of the graph
# Returns:
#   - The maximum degree found in the graph
function Get-MaxDegree {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [array]$adj
    )

    $maxDegree = 0
    for ($i = 1; $i -lt $adj.Count; $i++) {
        if ($adj[$i].Count -gt $maxDegree) {
            $maxDegree = $adj[$i].Count
        }
    }
    return $maxDegree
}

# Get-MinColors calculates the minimum number of colors needed to color the graph.
# The number of colors is equal to the maximum degree if it's odd, or max degree + 1 if even.
# Parameters:
#   - $maxDegree: The maximum degree of any vertex in the graph
# Returns:
#   - The minimum number of colors required
function Get-MinColors {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$maxDegree
    )

    if ($maxDegree % 2 -eq 1) {
        return $maxDegree
    }
    return $maxDegree + 1
}

# Color-Graph assigns colors to each vertex such that no two adjacent vertices share the same color.
# Parameters:
#   - $adj: The adjacency list representation of the graph
#   - $k: The number of colors to use
# Returns:
#   - An array representing the color assigned to each vertex (1-based indexing)
function Color-Graph {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [array]$adj,

        [Parameter(Mandatory = $true)]
        [int]$k
    )

    $color = @(0) * ($adj.Count)  # 1-based indexing
    for ($u = 1; $u -lt $adj.Count; $u++) {
        $used = @{}
        foreach ($v in $adj[$u]) {
            if ($color[$v] -ne 0) {
                $used[$color[$v]] = $true
            }
        }
        for ($c = 1; $c -le $k; $c++) {
            if (-not $used.ContainsKey($c)) {
                $color[$u] = $c
                break
            }
        }
    }
    return $color
}
