function Test-SquarePossibility {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string]$InputString
    )

    # Convert each character to its position in the alphabet (0-25)
    $perm = $InputString.ToUpper().ToCharArray() | ForEach-Object { [int][char]$_ - [int][char]'A' }

    # Initialize visited array and counts dictionary
    $visited = New-Object bool[] 26
    $counts = @{}

    for ($i = 0; $i -lt 26; $i++) {
        if (-not $visited[$i]) {
            $cnt = 0
            $j = $i
            while (-not $visited[$j]) {
                $visited[$j] = $true
                $j = $perm[$j]
                $cnt++
            }
            if ($counts.ContainsKey($cnt)) {
                $counts[$cnt]++
            } else {
                $counts[$cnt] = 1
            }
        }
    }

    # Check for even-length cycles with odd counts
    foreach ($length in $counts.Keys) {
        if ($length % 2 -eq 0 -and $counts[$length] % 2 -ne 0) {
            return $false
        }
    }

    return $true
}

