# Calculate-InkUsage calculates the total ink usage based on the given values and width.
# The function takes an array of values and a width, then computes the ink usage according to the specified algorithm.
function Calculate-InkUsage {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Values,
        [Parameter(Mandatory = $true)]
        [int]$Width
    )

    $vMax = ($Values | Measure-Object -Maximum).Maximum
    $m = [Math]::Floor($vMax / $Width) + 1
    $counts = New-Object int[] $m

    foreach ($v in $Values) {
        $i = [Math]::Floor($v / $Width)
        $counts[$i]++
    }

    $maxCount = ($counts | Measure-Object -Maximum).Maximum
    $sumContribution = 0.0

    for ($i = 0; $i -lt $m; $i++) {
        if ($maxCount -eq 0) {
            $contribution = 0.0
        } else {
            $contribution = ($counts[$i] / $maxCount) * (($m - 1 - $i) / ($m - 1))
        }
        $sumContribution += $contribution
    }

    $totalInk = $sumContribution + 0.01
    return $totalInk
}

