# Find-ClosestPairToProduct finds the pair of numbers in an array whose product is closest to a given target value P.
# If there are multiple pairs with the same difference, it returns the pair with the smallest first element, and if those are equal, the smallest second element.
function Find-ClosestPairToProduct {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$n,
        [Parameter(Mandatory = $true)]
        [int]$P,
        [Parameter(Mandatory = $true)]
        [int[]]$numbers
    )

    # Sort the numbers array to facilitate finding the smallest pair in case of ties
    $sortedNumbers = $numbers | Sort-Object

    $closestPair = [PSCustomObject]@{
        First = [int]::MaxValue
        Second = [int]::MaxValue
    }
    $closestDiff = [int]::MaxValue

    # Iterate through all possible pairs
    for ($i = 0; $i -lt $n; $i++) {
        for ($j = $i + 1; $j -lt $n; $j++) {
            $a = $sortedNumbers[$i]
            $b = $sortedNumbers[$j]
            $currentProduct = $a * $b
            $currentDiff = [Math]::Abs($currentProduct - $P)

            # Check if the current pair is closer or if it's a tie with a smaller pair
            if ($currentDiff -lt $closestDiff -or 
                ($currentDiff -eq $closestDiff -and 
                 ($a -lt $closestPair.First -or 
                  ($a -eq $closestPair.First -and $b -lt $closestPair.Second)))) {
                $closestDiff = $currentDiff
                $closestPair.First = $a
                $closestPair.Second = $b
            }
        }
    }

    return $closestPair
}
function Test-FindClosestPairToProduct {
    # Test case 1
    $result1 = Find-ClosestPairToProduct -n 4 -P 10 -numbers @(2, 3, 5, 7)
