# PowerShell Conversion of GCD and Maximum Division Logic

# Calculate-GCD computes the greatest common divisor (GCD) of an array of integers.
# It iteratively computes GCD of all elements in the array.
function Calculate-GCD {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Numbers
    )

    if ($Numbers.Count -eq 0) {
        return 0
    }

    $currentGCD = $Numbers[0]
    for ($i = 1; $i -lt $Numbers.Count; $i++) {
        $currentGCD = Get-GCD -a $currentGCD -b $Numbers[$i]
        if ($currentGCD -eq 1) {
            break  # GCD can't be smaller than 1
        }
    }
    return $currentGCD
}

# Get-GCD is a helper function that calculates the GCD of two numbers using the Euclidean algorithm.
function Get-GCD {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$a,
        [Parameter(Mandatory = $true)]
        [int]$b
    )

    while ($b -ne 0) {
        $temp = $b
        $b = $a % $b
        $a = $temp
    }
    return $a
}

# Solve-Problem is the main function that implements the core logic:
# 1. Calculates GCD of all numbers in the array
# 2. Divides the maximum number by this GCD
function Solve-Problem {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Numbers
    )

    if ($Numbers.Count -eq 0) {
        return 0
    }

    $gcd = Calculate-GCD -Numbers $Numbers
    $maxNumber = $Numbers | Measure-Object -Maximum | Select-Object -ExpandProperty Maximum
    return [math]::Floor($maxNumber / $gcd)
}

