# Find-BestMovieIndex selects the best movie index based on the audio and subtitle language preferences.
# It counts the occurrences of each language in the audience's preferences and compares them with the movie's audio and subtitle languages.
function Find-BestMovieIndex {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$AudienceLanguages,

        [Parameter(Mandatory = $true)]
        [int[]]$MovieAudioLanguages,

        [Parameter(Mandatory = $true)]
        [int[]]$MovieSubtitleLanguages
    )

    # Count the occurrences of each language in the audience's preferences
    $count = @{}
    foreach ($lang in $AudienceLanguages) {
        if ($count.ContainsKey($lang)) {
            $count[$lang]++
        } else {
            $count[$lang] = 1
        }
    }

    $maxAudio = -1
    $maxSub = -1
    $bestIndex = 0

    # Iterate through each movie to find the best match
    for ($i = 0; $i -lt $MovieAudioLanguages.Count; $i++) {
        $currentB = $MovieAudioLanguages[$i]
        $currentC = $MovieSubtitleLanguages[$i]

        $audio = if ($count.ContainsKey($currentB)) { $count[$currentB] } else { 0 }
        $sub = if ($count.ContainsKey($currentC)) { $count[$currentC] } else { 0 }

        if ($audio -gt $maxAudio -or ($audio -eq $maxAudio -and $sub -gt $maxSub)) {
            $maxAudio = $audio
            $maxSub = $sub
            $bestIndex = $i + 1  # Convert to 1-based index
        }
    }

    return $bestIndex
}

