function Get-MinOperationsToTransform {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string]$Initial,

        [Parameter(Mandatory = $true)]
        [string]$Target,

        [Parameter(Mandatory = $true)]
        [array]$Transformations
    )

    # Build adjacency list
    $adjList = @{}
    foreach ($transformation in $Transformations) {
        $start, $end, $steps = $transformation
        if (-not $adjList.ContainsKey($start)) {
            $adjList[$start] = @()
        }
        $adjList[$start] += @(,@($end, $steps))
    }

    # Initialize queue and visited set
    $queue = [System.Collections.Generic.Queue[System.Object]]::new()
    $queue.Enqueue(@($Initial, 0))
    $visited = @{}
    $visited[$Initial] = $true

    # Perform BFS
    while ($queue.Count -gt 0) {
        $current, $currentSteps = $queue.Dequeue()

        if ($current -eq $Target) {
            return $currentSteps
        }

        if ($adjList.ContainsKey($current)) {
            foreach ($neighborInfo in $adjList[$current]) {
                $neighbor, $steps = $neighborInfo
                if (-not $visited.ContainsKey($neighbor)) {
                    $visited[$neighbor] = $true
                    $queue.Enqueue(@($neighbor, $currentSteps + $steps))
                }
            }
        }
    }

    return -1
}

