function Find-MinimumValueAndWinner {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$A,
        [Parameter(Mandatory = $true)]
        [int]$B,
        [Parameter(Mandatory = $true)]
        [int]$C,
        [Parameter(Mandatory = $true)]
        [int]$D
    )

    $minVal = [Math]::Min($A, [Math]::Min($B, [Math]::Min($C, $D)))
    $count = 0
    $winner = @()

    if ($A -eq $minVal) {
        $count++
        $winner += "A"
    }
    if ($B -eq $minVal) {
        $count++
        $winner += "B"
    }
    if ($C -eq $minVal) {
        $count++
        $winner += "C"
    }
    if ($D -eq $minVal) {
        $count++
        $winner += "D"
    }

    if ($count -gt 1) {
        return "Equal"
    } else {
        return $winner[0]
    }
}

