function Get-MinimumPaintLiters {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$NumberOfWalls,

        [Parameter(Mandatory = $true)]
        [System.Collections.Generic.List[System.Tuple[int, int]]]$Dimensions
    )

    $totalArea = 0
    foreach ($dimension in $Dimensions) {
        $totalArea += $dimension.Item1 * $dimension.Item2
    }

    return $totalArea
}

