max_tasks <- function(tasks, T1, T2) {
  # Filter tasks to those within the time window [T1, T2]
  filtered_tasks <- tasks[sapply(tasks, function(task) task[1] >= T1 && task[2] <= T2)]

  # If no tasks are within the window, return 0
  if (length(filtered_tasks) == 0) {
    return(0)
  }

  # Convert the list of tasks into a matrix for easier sorting
  task_matrix <- matrix(unlist(filtered_tasks), ncol = 2, byrow = TRUE)

  # Sort tasks by end time
  task_matrix <- task_matrix[order(task_matrix[, 2]), ]

  count <- 0
  last_end_time <- T1

  # Iterate through the sorted tasks and count non-overlapping ones
  for (i in 1:nrow(task_matrix)) {
    start <- task_matrix[i, 1]
    end <- task_matrix[i, 2]

    if (start >= last_end_time) {
      count <- count + 1
      last_end_time = end
    }
  }

  return(count)
}

