# max_unique_activities identifies the user with the maximum number of unique activities within a specified time range.
# The function takes a list of logs, where each log is a vector containing userId, activityId, and timestamp.
# It also takes start and end timestamps to define the time range of interest.
# The function returns the userId of the user with the most unique activities in the specified time range.
# If no activities fall within the time range, it returns NULL.
max_unique_activities <- function(logs, start, end) {
  activity_count <- list()

  for (log in logs) {
    userId <- log[1]
    activityId <- log[2]
    timestamp <- log[3]
    if (timestamp >= start && timestamp <= end) {
      if (is.null(activity_count[[userId]])) {
        activity_count[[userId]] <- list()
      }
      if (!(activityId %in% activity_count[[userId]])) {
        activity_count[[userId]] <- c(activity_count[[userId]], activityId)
      }
    }
  }

  max_activities <- 0
  user_with_max_activities <- NULL

  for (userId in names(activity_count)) {
    current_activities <- length(activity_count[[userId]])
    if (current_activities > max_activities) {
      max_activities <- current_activities
      user_with_max_activities <- userId
    }
  }

  return(user_with_max_activities)
}

