minimal_subarray_length <- function(a, n, x) {
    left <- 1  # R uses 1-based indexing
    curr_sum <- 0
    min_len <- Inf

    for (right in 1:n) {
        curr_sum <- curr_sum + a[right]

        while (curr_sum >= x) {
            min_len <- min(min_len, right - left + 1)
            curr_sum <- curr_sum - a[left]
            left <- left + 1
        }
    }

    if (min_len != Inf) {
        return(min_len)
    } else {
        return(-1)
    }
}

