# find_pattern_indices returns a vector of starting indices in the text where any of the patterns are found.
# The function searches for each pattern in the text and records all starting positions of matches.
# The indices are sorted in ascending order to reflect their order of occurrence in the text.
find_pattern_indices <- function(patterns, text) {
  indices <- c()
  for (pattern in patterns) {
    start <- 1  # R strings are 1-indexed
    while (start <= nchar(text)) {
      # Find the pattern in the text starting from 'start'
      pos <- regexpr(pattern, substring(text, start, nchar(text)))
      if (pos == -1) {
        break
      }
      # Adjust the position to be relative to the entire text
      absolute_pos <- start + pos - 1
      indices <- c(indices, absolute_pos)
      start <- absolute_pos + 1  # Move past this match to find subsequent ones
    }
  }
  return(sort(indices))  # Return sorted indices
}

