# replace_vowels_with_ascii replaces vowels in the input string with their corresponding ASCII decimal values.
# The function checks each character in the input string and replaces it with its ASCII value if it is a vowel (case-insensitive).
# Non-vowel characters remain unchanged.
replace_vowels_with_ascii <- function(s) {
    vowels_to_ascii <- list(
        'A' = '65', 'a' = '65',
        'E' = '69', 'e' = '69',
        'I' = '73', 'i' = '73',
        'O' = '79', 'o' = '79',
        'U' = '85', 'u' = '85'
    )

    result <- c()
    for (char in strsplit(s, "")[[1]]) {
        if (char %in% names(vowels_to_ascii)) {
            result <- c(result, vowels_to_ascii[[char]])
        } else {
            result <- c(result, char)
        }
    }

    return(paste(result, collapse = ""))
}

