# adjust_positions rearranges the ages in a list such that people older than or equal to the threshold 
# remain in their original positions, while the rest are rearranged in non-descending order.
#
# Args:
#   ages: A numeric vector representing the ages of people.
#   threshold: A numeric value representing the age threshold.
#
# Returns:
#   A numeric vector with the ages adjusted according to the specified conditions.
adjust_positions <- function(ages, threshold) {
    # Collect the ages that can be rearranged (those below the threshold)
    rearrange_ages <- ages[ages < threshold]
    # Sort these ages in non-descending order
    rearrange_ages <- sort(rearrange_ages)

    # Initialize the result vector
    result <- numeric(length(ages))
    i <- 1  # Index for rearrange_ages

    # Iterate over the original ages and build the result vector
    for (j in 1:length(ages)) {
        if (ages[j] < threshold) {
            result[j] <- rearrange_ages[i]
            i <- i + 1
        } else {
            result[j] <- ages[j]
        }
    }

    return(result)
}

