is_path_exists <- function(matrix, N, M) {
  if (matrix[1, 1] == '#' || matrix[N, M] == '#') {
    return("NO")
  }

  # Nested DFS function to explore paths
  dfs <- function(x, y) {
    if (x == N && y == M) {
      return(TRUE)
    }
    if (x < 1 || y < 1 || x > N || y > M || matrix[x, y] == '#') {
      return(FALSE)
    }

    # Mark as visited by converting to obstacle
    matrix[x, y] <<- '#'

    # Check right and down for possible paths
    if (dfs(x + 1, y) || dfs(x, y + 1)) {
      return(TRUE)
    }

    return(FALSE)
  }

  if (dfs(1, 1)) {
    return("YES")
  } else {
    return("NO")
  }
}

process_test_cases <- function(test_cases) {
  results <- c()
  for (case in test_cases) {
    N <- case$N
    M <- case$M
    matrix <- case$matrix
    results <- c(results, is_path_exists(matrix, N, M))
  }
  return(results)
}

