remove_and_minimize <- function(S, ch) {
    # Convert the string to a vector of characters for easier manipulation
    chars <- unlist(strsplit(S, ""))

    # Find all indices where the character `ch` occurs
    indices <- which(chars == ch)

    # If `ch` is not found, return the original string
    if (length(indices) == 0) {
        return(S)
    }

    # Initialize the smallest string as NULL
    smallest_str <- NULL

    # Iterate over each occurrence of `ch` and construct new strings by removing it
    for (i in indices) {
        new_chars <- chars[-i]
        new_str <- paste(new_chars, collapse = "")

        # Update the smallest string if the new string is smaller or if it's the first candidate
        if (is.null(smallest_str) || new_str < smallest_str) {
            smallest_str <- new_str
        }
    }

    return(smallest_str)
}

