multiply_matrices <- function(mat1, mat2) {
  m <- nrow(mat1)
  n <- ncol(mat1)
  k <- nrow(mat2)
  p <- ncol(mat2)

  if (n != k) {
    return(list())
  }

  # Initialize the result matrix with zeros
  result <- matrix(0, nrow = m, ncol = p)

  # Perform matrix multiplication
  for (i in 1:m) {
    for (j in 1:n) {
      if (mat1[i, j] != 0) {
        for (l in 1:p) {
          result[i, l] <- result[i, l] + mat1[i, j] * mat2[j, l]
        }
      }
    }
  }

  return(result)
}

prim_mst <- function(n, dist_matrix) {
  min_cost <- rep(.Machine$integer.max, n)
  min_cost[1] <- 0  # R uses 1-based indexing
  visited <- rep(FALSE, n)
  pq <- list()
  pq[[1]] <- c(0, 1)  # (cost, vertex)

  total_cost <- 0

  while (length(pq) > 0) {
    # Extract the vertex with the minimum cost
    min_idx <- which.min(sapply(pq, function(x) x[1]))
    cost <- pq[[min_idx]][1]
    u <- pq[[min_idx]][2]
    pq <- pq[-min_idx]

    if (visited[u]) {
      next
    }
    visited[u] <- TRUE
    total_cost <- total_cost + cost

    for (v in 1:n) {
      if (!visited[v] && dist_matrix[u, v] < min_cost[v]) {
        min_cost[v] <- dist_matrix[u, v]
        pq[[length(pq) + 1]] <- c(dist_matrix[u, v], v)
      }
    }
  }

  return(total_cost)
}

minimal_rope_length <- function(test_cases) {
  results <- c()
  for (case in test_cases) {
    n <- case[[1]]
    dist_matrix <- case[[2]]
    results <- c(results, prim_mst(n, dist_matrix))
  }
  return(results)
}

