max_comfort_level <- function(n, m, r, comfort_levels, gina_prefs, paul_prefs) {
  # Convert preferences to sets for quick lookup
  gina_prefs_set <- gina_prefs
  paul_prefs_set <- paul_prefs

  # Create a data frame of comfort levels and seat indices (1-based)
  comfort_seats <- data.frame(
    comfort = comfort_levels,
    seat = 1:n
  )

  # Sort by comfort level in descending order
  comfort_seats <- comfort_seats[order(-comfort_seats$comfort), ]

  # Function to check if a combination of seats meets the preference constraints
  is_valid_combination <- function(combo) {
    gina_count <- sum(combo$seat %in% gina_prefs_set)
    paul_count <- sum(combo$seat %in% paul_prefs_set)
    return(gina_count >= r && paul_count >= r)
  }

  # Generate all possible combinations of m seats
  # Note: This can be computationally expensive for large n and m
  max_comfort <- -1
  indices <- combn(n, m, simplify = FALSE)

  for (idx in indices) {
    current_combo <- comfort_seats[idx, ]
    if (is_valid_combination(current_combo)) {
      total_comfort <- sum(current_combo$comfort)
      if (total_comfort > max_comfort) {
        max_comfort <- total_comfort
      }
    }
  }

  return(max_comfort)
}

