find_contiguous_subarrays <- function(arr, k) {
    if (length(arr) < k) {
        return(list())
    }

    result <- list()
    for (i in 1:(length(arr) - k + 1)) {
        result[[i]] <- arr[i:(i + k - 1)]
    }

    return(result)
}

