can_form_guards_pattern <- function(num_cases, cases) {
  results <- c()

  for (case in cases) {
    n <- case[[1]]
    heights <- case[[2]]

    if (n < 3) {
      results <- c(results, "NO")
      next
    }

    peak_found <- FALSE
    valid <- TRUE

    for (i in 2:n) {
      if (heights[i] == heights[i-1]) {
        valid <- FALSE
        break
      } else if (!peak_found) {
        if (heights[i] < heights[i-1]) {  # Start of decreasing part
          peak_found <- TRUE
        }
      } else {
        if (heights[i] > heights[i-1]) {  # Found an increasing part after peak
          valid <- FALSE
          break
        }
      }
    }

    if (valid && peak_found) {
      results <- c(results, "YES")
    } else {
      results <- c(results, "NO")
    }
  }

  return(results)
}

