longest_increasing_path <- function(grid) {
  if (length(grid) == 0 || length(grid[[1]]) == 0) {
    return(0)
  }

  rows <- length(grid)
  cols <- length(grid[[1]])
  memo <- matrix(-1, nrow = rows, ncol = cols)

  # dfs performs a depth-first search to find the longest increasing path starting from (x, y).
  dfs <- function(x, y) {
    if (memo[x, y] != -1) {
      return(memo[x, y])
    }

    directions <- list(c(1, 0), c(-1, 0), c(0, 1), c(0, -1))
    max_path <- 1
    for (dir in directions) {
      nx <- x + dir[1]
      ny <- y + dir[2]
      if (nx >= 1 && nx <= rows && ny >= 1 && ny <= cols && grid[[nx]][ny] > grid[[x]][y]) {
        max_path <- max(max_path, 1 + dfs(nx, ny))
      }
    }

    memo[x, y] <<- max_path
    return(max_path)
  }

  longest_path <- 0
  for (i in 1:rows) {
    for (j in 1:cols) {
      longest_path <- max(longest_path, dfs(i, j))
    }
  }

  return(longest_path)
}

