# longest_increasing_temperatures calculates the length of the longest contiguous subsequence 
# of increasing temperatures in a given numeric vector.
# 
# Parameters:
#   temperatures (numeric vector): A vector of daily temperatures.
#
# Returns:
#   integer: The length of the longest contiguous increasing subsequence.
longest_increasing_temperatures <- function(temperatures) {
  if (length(temperatures) == 0) {
    return(0)  # Edge case: no temperatures provided
  }

  # Initial parameters for tracking the longest increasing sequence
  max_length <- 1
  current_length <- 1

  for (i in 2:length(temperatures)) {
    if (temperatures[i] > temperatures[i - 1]) {
      current_length <- current_length + 1
      max_length <- max(max_length, current_length)
    } else {
      current_length <- 1
    }
  }

  return(max_length)
}

